import styled from 'styled-components';

export const TransactionHistoryStyle = styled.table`
  border: 1px solid grey;
  width: 400px;
  box-shadow: 3px 3px 15px 0px rgba(135, 206, 250, 0.2);
`;

export const TableHeadStyle = styled.thead`
  background-color: rgb(0, 206, 209);
`;

export const TableRowStyle = styled.th`
  border: 1px solid white;
  padding: 5px;
`;

export const TableBodyRowStyle = styled.td`
  border: 1px solid white;
  text-align: center;
  padding: 5px;
  background-color: rgb(224, 255, 255);
  font-size: 20px;
`;
